CREATE TABLE `hrs_permission` (
  `permission_id` int(11) NOT NULL AUTO_INCREMENT,
  `permission_code` varchar(20) NOT NULL,
  `permission_name` varchar(40) NOT NULL,
  `path` varchar(255) DEFAULT NULL COMMENT '前端链接，如果 路径是 /unDevelop 则 跳转到 暂未开通页面',
  `url` varchar(255) DEFAULT NULL COMMENT 'vue链接',
  `permission` varchar(255) DEFAULT NULL,
  `icon` varchar(255) DEFAULT NULL,
  `parent_id` int(11) DEFAULT NULL,
  `parent_ids` varchar(255) DEFAULT NULL,
  `sort` int(11) DEFAULT NULL,
  `status` int(1) DEFAULT '0' COMMENT '0:启用 1:删除',
  `permission_type` varchar(20) NOT NULL,
  `creator_id` int(11) DEFAULT NULL,
  `create_time` datetime NOT NULL,
  `stop_date` datetime DEFAULT NULL,
  `app_code` varchar(20) NOT NULL,
  `disabled` varchar(20) DEFAULT NULL COMMENT '不可分配',
  `admin_default` varchar(20) DEFAULT NULL COMMENT '默认给管理员',
  `able_Orgid` varchar(20) DEFAULT NULL,
  `help_document_name` varchar(30) DEFAULT NULL COMMENT '帮助文档名称',
  `help_document_url` varchar(100) DEFAULT NULL COMMENT '帮助文档路径',
  `customized` bit(1) DEFAULT b'0' COMMENT '定制菜单',
  `url_crud` tinyint(4) DEFAULT NULL,
  `mapping_url` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`permission_id`) USING BTREE,
  KEY `IDU_hrs_permission_SY_01` (`permission_id`,`path`,`url`,`parent_id`,`sort`,`permission`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=1303 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='HRS 权限管理';




